mapper.delete:
		delete from tab_role_menu where menuId in

            #{menu.id}

		and roleId=#{rid}

mapper.select:
select id from tab_menu

where code is not null
where code is null and deleteFlag = #{deleteFlag}
order by menuOrder asc;

mapper.delete:
delete from tab_menu where id in(-1

	#{menu.id}

)

mapper.select:
select tm.* from tab_menu tm
join tab_role_menu trm on trm.menuId = tm.id
where tm.url = #{url}
AND trm.roleId=#{rid}
limit 1
