mapper.select:
select id from tab_user where create_by = #{uid} and

	delete_flag is null


	delete_flag = #{deleteFlag}


	order by ${orderBy}

limit ${curcount},${ps}

select count(*)from tab_user where create_by = #{uid} and

	delete_flag is null


	delete_flag = #{deleteFlag}

mapper.delete:
delete from tab_user where id in (-1,

	#{id}

)

mapper.update:
update tab_user set delete_flag = true where id in (-1,

	#{id}

)

mapper.select:
SELECT u.*,r.role_name FROM tab_user u LEFT JOIN tab_role r ON u.role_id = r.id where
u.delete_flag = false
u.delete_flag = #{delete_flag}
limit ${offset},${pageSize}

select count(u.id) from tab_user u where
u.delete_flag = false
u.delete_flag = #{delete_flag}
